contracts/FundRequest.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title FundRequest
 * @dev Smart contract for managing vendor fund requests and BPPA officer approvals
 */
contract FundRequest {
    
    enum RequestStatus {
        Pending,        // Submitted, awaiting review
        Approved,       // Approved by BPPA officer
        Rejected,       // Rejected by BPPA officer
        Funded,         // Funds disbursed
        Cancelled       // Request cancelled
    }
    
    struct FundRequestDetails {
        string requestId;           // Unique request ID
        string procurementId;       // Related procurement ID
        string vendorId;            // Requesting vendor ID
        string companyName;         // Vendor company name
        uint256 requestedAmount;    // Amount requested in BDT
        string reason;              // Reason for additional funds
        string justification;       // Detailed justification
        string supportingDocs;      // IPFS hash for supporting documents
        uint256 submittedTimestamp; // When request was submitted
        uint256 reviewedTimestamp;  // When request was reviewed
        address bppaOfficer;        // BPPA officer who reviewed
        RequestStatus status;       // Current status
        string bppaComments;        // BPPA officer's comments
        uint256 approvedAmount;     // Amount approved (may be less than requested)
        string disbursementRef;     // Reference for fund disbursement
    }
    
    // State variables
    mapping(string => FundRequestDetails) public fundRequests;
    mapping(string => string[]) public procurementFundRequests; // procurement -> request IDs
    mapping(string => string[]) public vendorFundRequests;      // vendor -> request IDs
    mapping(address => string[]) public officerReviewedRequests; // officer -> request IDs
    
    // Access control
    mapping(address => bool) public isVendor;
    mapping(address => bool) public isBppaOfficer;
    mapping(address => bool) public isAdmin;
    
    // Arrays for enumeration
    string[] public allRequestIds;
    
    // Events
    event FundRequestSubmitted(string indexed requestId, string procurementId, string vendorId, uint256 amount);
    event FundRequestApproved(string indexed requestId, address bppaOfficer, uint256 approvedAmount);
    event FundRequestRejected(string indexed requestId, address bppaOfficer, string reason);
    event FundsDisbursed(string indexed requestId, uint256 amount, string disbursementRef);
    event FundRequestCancelled(string indexed requestId, string reason);
    event VendorRegistered(address indexed vendor, string vendorId);
    event BppaOfficerRegistered(address indexed officer);
    
    // Modifiers
    modifier onlyVendor() {
        require(isVendor[msg.sender], "Only registered vendors can perform this action");
        _;
    }
    
    modifier onlyBppaOfficer() {
        require(isBppaOfficer[msg.sender], "Only BPPA officers can perform this action");
        _;
    }
    
    modifier onlyAdmin() {
        require(isAdmin[msg.sender], "Only admin can perform this action");
        _;
    }
    
    modifier validRequest(string memory _requestId) {
        require(bytes(fundRequests[_requestId].requestId).length > 0, "Fund request not found");
        _;
    }
    
    /**
     * @dev Constructor - sets deployer as admin
     */
    constructor() {
        isAdmin[msg.sender] = true;
    }
    
    /**
     * @dev Register vendor (admin function or self-registration)
     */
    function registerVendor(address _vendor, string memory _vendorId) external {
        require(_vendor != address(0), "Invalid vendor address");
        require(bytes(_vendorId).length > 0, "Vendor ID cannot be empty");
        require(msg.sender == _vendor || isAdmin[msg.sender], "Can only register self or admin can register");
        
        isVendor[_vendor] = true;
        emit VendorRegistered(_vendor, _vendorId);
    }
    
    /**
     * @dev Register BPPA officer (admin function)
     */
    function registerBppaOfficer(address _officer) external onlyAdmin {
        require(_officer != address(0), "Invalid officer address");
        isBppaOfficer[_officer] = true;
        emit BppaOfficerRegistered(_officer);
    }
    
    /**
     * @dev Submit a fund request (vendors only)
     */
    function submitFundRequest(
        string memory _requestId,
        string memory _procurementId,
        string memory _vendorId,
        string memory _companyName,
        uint256 _requestedAmount,
        string memory _reason,
        string memory _justification,
        string memory _supportingDocs
    ) external onlyVendor {
        require(bytes(_requestId).length > 0, "Request ID cannot be empty");
        require(bytes(fundRequests[_requestId].requestId).length == 0, "Request ID already exists");
        require(bytes(_procurementId).length > 0, "Procurement ID cannot be empty");
        require(bytes(_vendorId).length > 0, "Vendor ID cannot be empty");
        require(_requestedAmount > 0, "Requested amount must be positive");
        require(bytes(_reason).length > 0, "Reason cannot be empty");
        
        fundRequests[_requestId] = FundRequestDetails({
            requestId: _requestId,
            procurementId: _procurementId,
            vendorId: _vendorId,
            companyName: _companyName,
            requestedAmount: _requestedAmount,
            reason: _reason,
            justification: _justification,
            supportingDocs: _supportingDocs,
            submittedTimestamp: block.timestamp,
            reviewedTimestamp: 0,
            bppaOfficer: address(0),
            status: RequestStatus.Pending,
            bppaComments: "",
            approvedAmount: 0,
            disbursementRef: ""
        });
        
        // Add to tracking arrays
        allRequestIds.push(_requestId);
        procurementFundRequests[_procurementId].push(_requestId);
        vendorFundRequests[_vendorId].push(_requestId);
        
        emit FundRequestSubmitted(_requestId, _procurementId, _vendorId, _requestedAmount);
    }
    
    /**
     * @dev Approve fund request (BPPA officers only)
     */
    function approveFundRequest(
        string memory _requestId,
        uint256 _approvedAmount,
        string memory _bppaComments
    ) external onlyBppaOfficer validRequest(_requestId) {
        require(fundRequests[_requestId].status == RequestStatus.Pending, "Request not pending");
        require(_approvedAmount > 0, "Approved amount must be positive");
        require(_approvedAmount <= fundRequests[_requestId].requestedAmount, "Cannot approve more than requested");
        
        fundRequests[_requestId].status = RequestStatus.Approved;
        fundRequests[_requestId].bppaOfficer = msg.sender;
        fundRequests[_requestId].reviewedTimestamp = block.timestamp;
        fundRequests[_requestId].bppaComments = _bppaComments;
        fundRequests[_requestId].approvedAmount = _approvedAmount;
        
        // Add to officer's reviewed requests
        officerReviewedRequests[msg.sender].push(_requestId);
        
        emit FundRequestApproved(_requestId, msg.sender, _approvedAmount);
    }
    
    /**
     * @dev Reject fund request (BPPA officers only)
     */
    function rejectFundRequest(
        string memory _requestId,
        string memory _bppaComments
    ) external onlyBppaOfficer validRequest(_requestId) {
        require(fundRequests[_requestId].status == RequestStatus.Pending, "Request not pending");
        require(bytes(_bppaComments).length > 0, "Comments required for rejection");
        
        fundRequests[_requestId].status = RequestStatus.Rejected;
        fundRequests[_requestId].bppaOfficer = msg.sender;
        fundRequests[_requestId].reviewedTimestamp = block.timestamp;
        fundRequests[_requestId].bppaComments = _bppaComments;
        
        // Add to officer's reviewed requests
        officerReviewedRequests[msg.sender].push(_requestId);
        
        emit FundRequestRejected(_requestId, msg.sender, _bppaComments);
    }
    
    /**
     * @dev Mark funds as disbursed (BPPA officers or admin only)
     */
    function markFundsDisbursed(
        string memory _requestId,
        string memory _disbursementRef
    ) external validRequest(_requestId) {
        require(isBppaOfficer[msg.sender] || isAdmin[msg.sender], "Only BPPA officers or admin can mark funds disbursed");
        require(fundRequests[_requestId].status == RequestStatus.Approved, "Request not approved");
        require(bytes(_disbursementRef).length > 0, "Disbursement reference required");
        
        fundRequests[_requestId].status = RequestStatus.Funded;
        fundRequests[_requestId].disbursementRef = _disbursementRef;
        
        emit FundsDisbursed(_requestId, fundRequests[_requestId].approvedAmount, _disbursementRef);
    }
    
    /**
     * @dev Cancel fund request (vendor or admin only)
     */
    function cancelFundRequest(
        string memory _requestId,
        string memory _reason
    ) external validRequest(_requestId) {
        require(
            isVendor[msg.sender] || isAdmin[msg.sender], 
            "Only requesting vendor or admin can cancel"
        );
        require(
            fundRequests[_requestId].status == RequestStatus.Pending || 
            fundRequests[_requestId].status == RequestStatus.Approved,
            "Cannot cancel this request"
        );
        require(bytes(_reason).length > 0, "Cancellation reason required");
        
        fundRequests[_requestId].status = RequestStatus.Cancelled;
        
        emit FundRequestCancelled(_requestId, _reason);
    }
    
    /**
     * @dev Get fund request details
     */
    function getFundRequest(string memory _requestId) external view validRequest(_requestId) returns (
        string memory requestId,
        string memory procurementId,
        string memory vendorId,
        string memory companyName,
        uint256 requestedAmount,
        string memory reason,
        uint256 submittedTimestamp,
        RequestStatus status,
        uint256 approvedAmount,
        address bppaOfficer,
        string memory bppaComments
    ) {
        FundRequestDetails memory request = fundRequests[_requestId];
        return (
            request.requestId,
            request.procurementId,
            request.vendorId,
            request.companyName,
            request.requestedAmount,
            request.reason,
            request.submittedTimestamp,
            request.status,
            request.approvedAmount,
            request.bppaOfficer,
            request.bppaComments
        );
    }
    
    /**
     * @dev Get fund requests for a specific procurement
     */
    function getFundRequestsByProcurement(string memory _procurementId) external view returns (string[] memory) {
        return procurementFundRequests[_procurementId];
    }
    
    /**
     * @dev Get fund requests by a specific vendor
     */
    function getFundRequestsByVendor(string memory _vendorId) external view returns (string[] memory) {
        return vendorFundRequests[_vendorId];
    }
    
    /**
     * @dev Get fund requests reviewed by a specific officer
     */
    function getFundRequestsByOfficer(address _officer) external view returns (string[] memory) {
        return officerReviewedRequests[_officer];
    }
    
    /**
     * @dev Get all pending fund requests
     */
    function getPendingFundRequests() external view returns (string[] memory) {
        uint256 pendingCount = 0;
        
        // Count pending requests
        for (uint256 i = 0; i < allRequestIds.length; i++) {
            if (fundRequests[allRequestIds[i]].status == RequestStatus.Pending) {
                pendingCount++;
            }
        }
        
        // Create array of pending request IDs
        string[] memory pendingRequests = new string[](pendingCount);
        uint256 currentIndex = 0;
        
        for (uint256 i = 0; i < allRequestIds.length; i++) {
            if (fundRequests[allRequestIds[i]].status == RequestStatus.Pending) {
                pendingRequests[currentIndex] = allRequestIds[i];
                currentIndex++;
            }
        }
        
        return pendingRequests;
    }
    
    /**
     * @dev Get approved fund requests that are awaiting disbursement
     */
    function getApprovedFundRequests() external view returns (string[] memory) {
        uint256 approvedCount = 0;
        
        // Count approved requests
        for (uint256 i = 0; i < allRequestIds.length; i++) {
            if (fundRequests[allRequestIds[i]].status == RequestStatus.Approved) {
                approvedCount++;
            }
        }
        
        // Create array of approved request IDs
        string[] memory approvedRequests = new string[](approvedCount);
        uint256 currentIndex = 0;
        
        for (uint256 i = 0; i < allRequestIds.length; i++) {
            if (fundRequests[allRequestIds[i]].status == RequestStatus.Approved) {
                approvedRequests[currentIndex] = allRequestIds[i];
                currentIndex++;
            }
        }
        
        return approvedRequests;
    }
    
    /**
     * @dev Get total statistics
     */
    function getStatistics() external view returns (
        uint256 totalRequests,
        uint256 pendingRequests,
        uint256 approvedRequests,
        uint256 rejectedRequests,
        uint256 fundedRequests,
        uint256 totalRequestedAmount,
        uint256 totalApprovedAmount
    ) {
        uint256 pending = 0;
        uint256 approved = 0;
        uint256 rejected = 0;
        uint256 funded = 0;
        uint256 totalRequested = 0;
        uint256 totalApproved = 0;
        
        for (uint256 i = 0; i < allRequestIds.length; i++) {
            FundRequestDetails memory request = fundRequests[allRequestIds[i]];
            
            totalRequested += request.requestedAmount;
            
            if (request.status == RequestStatus.Pending) {
                pending++;
            } else if (request.status == RequestStatus.Approved) {
                approved++;
                totalApproved += request.approvedAmount;
            } else if (request.status == RequestStatus.Rejected) {
                rejected++;
            } else if (request.status == RequestStatus.Funded) {
                funded++;
                totalApproved += request.approvedAmount;
            }
        }
        
        return (
            allRequestIds.length,
            pending,
            approved,
            rejected,
            funded,
            totalRequested,
            totalApproved
        );
    }
    
    /**
     * @dev Get all request IDs
     */
    function getAllRequestIds() external view returns (string[] memory) {
        return allRequestIds;
    }
}

contracts/NationalLedger.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title NationalLedger
 * @dev Smart contract for tracking national revenue from verified tax returns
 * and expenses from awarded procurement bids
 */
contract NationalLedger {
    
    struct RevenueEntry {
        string tiin;                // Taxpayer ID
        uint256 amount;             // Tax amount in BDT (wei equivalent)
        uint256 timestamp;          // When added to revenue
        address validator;          // NBR officer who verified
        string taxReturnHash;       // Reference to tax return
        string fiscalYear;          // Tax year
    }
    
    struct ExpenseEntry {
        string procurementId;       // Procurement reference
        uint256 amount;             // Expense amount in BDT (wei equivalent)
        uint256 timestamp;          // When expense was recorded
        address bppaOfficer;        // BPPA officer who approved
        string vendorId;            // Winning vendor ID
        string description;         // Expense description
        bool isFundRequest;         // Whether this is additional fund request
    }
    
    // State variables
    uint256 public totalRevenue;
    uint256 public totalExpense;
    uint256 public availableBalance;
    
    // Arrays to store all entries
    RevenueEntry[] public revenueEntries;
    ExpenseEntry[] public expenseEntries;
    
    // Mappings for quick access
    mapping(string => uint256[]) public revenueByFiscalYear;
    mapping(string => uint256[]) public expenseByProcurement;
    mapping(address => uint256[]) public revenueByValidator;
    mapping(address => uint256[]) public expenseByBppaOfficer;
    
    // Access control
    mapping(address => bool) public isAuthorizedRevenueAdder;
    mapping(address => bool) public isAuthorizedExpenseAdder;
    mapping(address => bool) public isAdmin;
    
    // Events
    event RevenueAdded(string indexed tiin, uint256 amount, string fiscalYear, address validator);
    event ExpenseAdded(string indexed procurementId, uint256 amount, string vendorId, address bppaOfficer);
    event FundRequestExpenseAdded(string indexed procurementId, uint256 amount, string vendorId, address bppaOfficer);
    event AuthorizationGranted(address indexed account, string role);
    event AuthorizationRevoked(address indexed account, string role);
    
    // Modifiers
    modifier onlyAdmin() {
        require(isAdmin[msg.sender], "Only admin can perform this action");
        _;
    }
    
    modifier onlyAuthorizedRevenueAdder() {
        require(isAuthorizedRevenueAdder[msg.sender], "Not authorized to add revenue");
        _;
    }
    
    modifier onlyAuthorizedExpenseAdder() {
        require(isAuthorizedExpenseAdder[msg.sender], "Not authorized to add expense");
        _;
    }
    
    /**
     * @dev Constructor - sets deployer as initial admin
     */
    constructor() {
        isAdmin[msg.sender] = true;
        totalRevenue = 0;
        totalExpense = 0;
        availableBalance = 0;
    }
    
    /**
     * @dev Grant admin privileges
     */
    function grantAdmin(address _account) external onlyAdmin {
        isAdmin[_account] = true;
        emit AuthorizationGranted(_account, "admin");
    }
    
    /**
     * @dev Grant revenue adding privileges (for TaxReturnRegistry contract)
     */
    function grantRevenueAdder(address _account) external onlyAdmin {
        isAuthorizedRevenueAdder[_account] = true;
        emit AuthorizationGranted(_account, "revenue_adder");
    }
    
    /**
     * @dev Grant expense adding privileges (for Procurement/FundRequest contracts)
     */
    function grantExpenseAdder(address _account) external onlyAdmin {
        isAuthorizedExpenseAdder[_account] = true;
        emit AuthorizationGranted(_account, "expense_adder");
    }
    
    /**
     * @dev Revoke admin privileges
     */
    function revokeAdmin(address _account) external onlyAdmin {
        require(_account != msg.sender, "Cannot revoke own admin privileges");
        isAdmin[_account] = false;
        emit AuthorizationRevoked(_account, "admin");
    }
    
    /**
     * @dev Add revenue from verified tax return
     */
    function addRevenue(
        string memory _tiin,
        uint256 _amount,
        string memory _fiscalYear,
        address _validator,
        string memory _taxReturnHash
    ) external onlyAuthorizedRevenueAdder {
        require(_amount > 0, "Revenue amount must be positive");
        require(bytes(_tiin).length > 0, "TIIN cannot be empty");
        require(_validator != address(0), "Invalid validator address");
        
        RevenueEntry memory newEntry = RevenueEntry({
            tiin: _tiin,
            amount: _amount,
            timestamp: block.timestamp,
            validator: _validator,
            taxReturnHash: _taxReturnHash,
            fiscalYear: _fiscalYear
        });
        
        revenueEntries.push(newEntry);
        uint256 entryIndex = revenueEntries.length - 1;
        
        // Update mappings
        revenueByFiscalYear[_fiscalYear].push(entryIndex);
        revenueByValidator[_validator].push(entryIndex);
        
        // Update totals
        totalRevenue += _amount;
        availableBalance += _amount;
        
        emit RevenueAdded(_tiin, _amount, _fiscalYear, _validator);
    }
    
    /**
     * @dev Add expense from awarded procurement
     */
    function addExpense(
        string memory _procurementId,
        uint256 _amount,
        address _bppaOfficer,
        string memory _vendorId,
        string memory _description
    ) external onlyAuthorizedExpenseAdder {
        require(_amount > 0, "Expense amount must be positive");
        require(_amount <= availableBalance, "Insufficient balance for this expense");
        require(bytes(_procurementId).length > 0, "Procurement ID cannot be empty");
        require(_bppaOfficer != address(0), "Invalid BPPA officer address");
        
        ExpenseEntry memory newEntry = ExpenseEntry({
            procurementId: _procurementId,
            amount: _amount,
            timestamp: block.timestamp,
            bppaOfficer: _bppaOfficer,
            vendorId: _vendorId,
            description: _description,
            isFundRequest: false
        });
        
        expenseEntries.push(newEntry);
        uint256 entryIndex = expenseEntries.length - 1;
        
        // Update mappings
        expenseByProcurement[_procurementId].push(entryIndex);
        expenseByBppaOfficer[_bppaOfficer].push(entryIndex);
        
        // Update totals
        totalExpense += _amount;
        availableBalance -= _amount;
        
        emit ExpenseAdded(_procurementId, _amount, _vendorId, _bppaOfficer);
    }
    
    /**
     * @dev Add expense from approved fund request
     */
    function addFundRequestExpense(
        string memory _procurementId,
        uint256 _amount,
        address _bppaOfficer,
        string memory _vendorId,
        string memory _description
    ) external onlyAuthorizedExpenseAdder {
        require(_amount > 0, "Expense amount must be positive");
        require(_amount <= availableBalance, "Insufficient balance for this expense");
        require(bytes(_procurementId).length > 0, "Procurement ID cannot be empty");
        require(_bppaOfficer != address(0), "Invalid BPPA officer address");
        
        ExpenseEntry memory newEntry = ExpenseEntry({
            procurementId: _procurementId,
            amount: _amount,
            timestamp: block.timestamp,
            bppaOfficer: _bppaOfficer,
            vendorId: _vendorId,
            description: _description,
            isFundRequest: true
        });
        
        expenseEntries.push(newEntry);
        uint256 entryIndex = expenseEntries.length - 1;
        
        // Update mappings
        expenseByProcurement[_procurementId].push(entryIndex);
        expenseByBppaOfficer[_bppaOfficer].push(entryIndex);
        
        // Update totals
        totalExpense += _amount;
        availableBalance -= _amount;
        
        emit FundRequestExpenseAdded(_procurementId, _amount, _vendorId, _bppaOfficer);
    }
    
    /**
     * @dev Get current ledger summary
     */
    function getLedgerSummary() external view returns (
        uint256 revenue,
        uint256 expense,
        uint256 balance,
        uint256 totalRevenueEntries,
        uint256 totalExpenseEntries
    ) {
        return (
            totalRevenue,
            totalExpense,
            availableBalance,
            revenueEntries.length,
            expenseEntries.length
        );
    }
    
    /**
     * @dev Get revenue entry by index
     */
    function getRevenueEntry(uint256 _index) external view returns (
        string memory tiin,
        uint256 amount,
        uint256 timestamp,
        address validator,
        string memory taxReturnHash,
        string memory fiscalYear
    ) {
        require(_index < revenueEntries.length, "Revenue entry index out of bounds");
        RevenueEntry memory entry = revenueEntries[_index];
        return (
            entry.tiin,
            entry.amount,
            entry.timestamp,
            entry.validator,
            entry.taxReturnHash,
            entry.fiscalYear
        );
    }
    
    /**
     * @dev Get expense entry by index
     */
    function getExpenseEntry(uint256 _index) external view returns (
        string memory procurementId,
        uint256 amount,
        uint256 timestamp,
        address bppaOfficer,
        string memory vendorId,
        string memory description,
        bool isFundRequest
    ) {
        require(_index < expenseEntries.length, "Expense entry index out of bounds");
        ExpenseEntry memory entry = expenseEntries[_index];
        return (
            entry.procurementId,
            entry.amount,
            entry.timestamp,
            entry.bppaOfficer,
            entry.vendorId,
            entry.description,
            entry.isFundRequest
        );
    }
    
    /**
     * @dev Get revenue entries for a specific fiscal year
     */
    function getRevenueByFiscalYear(string memory _fiscalYear) external view returns (uint256[] memory) {
        return revenueByFiscalYear[_fiscalYear];
    }
    
    /**
     * @dev Get expense entries for a specific procurement
     */
    function getExpenseByProcurement(string memory _procurementId) external view returns (uint256[] memory) {
        return expenseByProcurement[_procurementId];
    }
    
    /**
     * @dev Get total revenue for a fiscal year
     */
    function getTotalRevenueByFiscalYear(string memory _fiscalYear) external view returns (uint256) {
        uint256[] memory entries = revenueByFiscalYear[_fiscalYear];
        uint256 total = 0;
        
        for (uint256 i = 0; i < entries.length; i++) {
            total += revenueEntries[entries[i]].amount;
        }
        
        return total;
    }
    
    /**
     * @dev Get total expense for a procurement
     */
    function getTotalExpenseByProcurement(string memory _procurementId) external view returns (uint256) {
        uint256[] memory entries = expenseByProcurement[_procurementId];
        uint256 total = 0;
        
        for (uint256 i = 0; i < entries.length; i++) {
            total += expenseEntries[entries[i]].amount;
        }
        
        return total;
    }
    
    /**
     * @dev Emergency function to adjust balance (admin only)
     */
    function emergencyAdjustBalance(uint256 _newBalance, string memory _reason) external onlyAdmin {
        availableBalance = _newBalance;
        // Note: This is for emergency corrections only and should be logged off-chain
    }
}

contracts/Procurement.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title Procurement
 * @dev Smart contract for managing procurement lifecycle including
 * publication, bid submission, L1/QCBS shortlisting, and awarding
 */
contract Procurement {
    
    enum ProcurementStatus {
        Created,        // Just created
        Published,      // Open for bid submission
        BidsReceived,   // Bids submitted, shortlisting pending
        ShortlistDone,  // Shortlisted, ready for voting
        VotingActive,   // Voting in progress
        Awarded,        // Contract awarded
        Completed,      // Project completed
        Cancelled       // Procurement cancelled
    }
    
    enum ShortlistingMethod {
        L1,     // Lowest cost
        QCBS    // Quality-Cost Based Selection
    }
    
    struct ProcurementDetails {
        string procurementId;       // Unique procurement ID
        string title;               // Procurement title
        string description;         // Detailed description
        uint256 estimatedValue;     // Estimated budget in BDT
        string category;            // Category (Infrastructure, IT, etc.)
        string requirements;        // Technical requirements
        uint256 bidDeadline;        // Bid submission deadline
        uint256 createdTimestamp;   // When created
        address bppaOfficer;        // Creating BPPA officer
        ProcurementStatus status;   // Current status
        ShortlistingMethod shortlistMethod; // L1 or QCBS
        uint256 maxBids;            // Maximum bids allowed
        uint256 shortlistCount;     // How many to shortlist (usually 3-5)
    }
    
    struct BidDetails {
        string vendorId;            // Vendor identification
        string companyName;         // Vendor company name
        uint256 bidAmount;          // Bid amount in BDT
        string technicalProposal;   // Technical proposal summary
        string costingDocument;     // IPFS hash for detailed costing
        uint256 completionDays;     // Proposed completion time
        uint256 submittedTimestamp; // When bid was submitted
        uint256 qualityScore;       // Quality score (for QCBS, 0-100)
        uint256 combinedScore;      // Combined score for QCBS
        bool isShortlisted;         // Whether shortlisted
        bool isWinner;              // Whether this bid won
        string remarks;             // BPPA officer remarks
    }
    
    struct QCBSWeights {
        uint256 costWeight;         // Weight for cost component (0-100)
        uint256 qualityWeight;      // Weight for quality component (0-100)
    }
    
    // State variables
    mapping(string => ProcurementDetails) public procurements;
    mapping(string => BidDetails[]) public procurementBids;
    mapping(string => uint256[]) public shortlistedBidIndices;
    mapping(string => uint256) public winningBidIndex;
    mapping(string => QCBSWeights) public qcbsWeights;
    
    // Access control
    mapping(address => bool) public isBppaOfficer;
    mapping(address => bool) public isVendor;
    mapping(address => bool) public isAdmin;
    
    // Arrays for enumeration
    string[] public allProcurementIds;
    
    // Events
    event ProcurementCreated(string indexed procurementId, string title, address bppaOfficer);
    event ProcurementPublished(string indexed procurementId, uint256 bidDeadline);
    event BidSubmitted(string indexed procurementId, string vendorId, uint256 bidAmount);
    event BidsShortlisted(string indexed procurementId, uint256[] shortlistedIndices, ShortlistingMethod method);
    event ProcurementAwarded(string indexed procurementId, uint256 winningBidIndex, string vendorId);
    event ProcurementStatusChanged(string indexed procurementId, ProcurementStatus newStatus);
    event BppaOfficerRegistered(address indexed officer);
    event VendorRegistered(address indexed vendor);
    
    // Modifiers
    modifier onlyBppaOfficer() {
        require(isBppaOfficer[msg.sender], "Only BPPA officers can perform this action");
        _;
    }
    
    modifier onlyVendor() {
        require(isVendor[msg.sender], "Only registered vendors can perform this action");
        _;
    }
    
    modifier onlyAdmin() {
        require(isAdmin[msg.sender], "Only admin can perform this action");
        _;
    }
    
    modifier validProcurement(string memory _procurementId) {
        require(bytes(procurements[_procurementId].procurementId).length > 0, "Procurement not found");
        _;
    }
    
    /**
     * @dev Constructor - sets deployer as admin
     */
    constructor() {
        isAdmin[msg.sender] = true;
    }
    
    /**
     * @dev Register BPPA officer (admin function)
     */
    function registerBppaOfficer(address _officer) external onlyAdmin {
        require(_officer != address(0), "Invalid officer address");
        isBppaOfficer[_officer] = true;
        emit BppaOfficerRegistered(_officer);
    }
    
    /**
     * @dev Register vendor (admin function or self-registration)
     */
    function registerVendor(address _vendor) external {
        require(_vendor != address(0), "Invalid vendor address");
        require(msg.sender == _vendor || isAdmin[msg.sender], "Can only register self or admin can register");
        isVendor[_vendor] = true;
        emit VendorRegistered(_vendor);
    }
    
    /**
     * @dev Create a new procurement (BPPA officer only)
     */
    function createProcurement(
        string memory _procurementId,
        string memory _title,
        string memory _description,
        uint256 _estimatedValue,
        string memory _category,
        string memory _requirements,
        ShortlistingMethod _shortlistMethod,
        uint256 _maxBids,
        uint256 _shortlistCount
    ) external onlyBppaOfficer {
        require(bytes(_procurementId).length > 0, "Procurement ID cannot be empty");
        require(bytes(procurements[_procurementId].procurementId).length == 0, "Procurement ID already exists");
        require(_estimatedValue > 0, "Estimated value must be positive");
        require(_maxBids > 0 && _maxBids <= 20, "Max bids must be between 1 and 20");
        require(_shortlistCount > 0 && _shortlistCount <= _maxBids, "Invalid shortlist count");
        
        procurements[_procurementId] = ProcurementDetails({
            procurementId: _procurementId,
            title: _title,
            description: _description,
            estimatedValue: _estimatedValue,
            category: _category,
            requirements: _requirements,
            bidDeadline: 0,
            createdTimestamp: block.timestamp,
            bppaOfficer: msg.sender,
            status: ProcurementStatus.Created,
            shortlistMethod: _shortlistMethod,
            maxBids: _maxBids,
            shortlistCount: _shortlistCount
        });
        
        // Set default QCBS weights (70% cost, 30% quality) if QCBS method
        if (_shortlistMethod == ShortlistingMethod.QCBS) {
            qcbsWeights[_procurementId] = QCBSWeights({
                costWeight: 70,
                qualityWeight: 30
            });
        }
        
        allProcurementIds.push(_procurementId);
        
        emit ProcurementCreated(_procurementId, _title, msg.sender);
    }
    
    /**
     * @dev Publish procurement for bid submission (BPPA officer only)
     */
    function publishProcurement(
        string memory _procurementId,
        uint256 _bidSubmissionDays
    ) external onlyBppaOfficer validProcurement(_procurementId) {
        require(procurements[_procurementId].status == ProcurementStatus.Created, "Procurement already published");
        require(_bidSubmissionDays > 0 && _bidSubmissionDays <= 365, "Invalid bid submission period");
        
        uint256 bidDeadline = block.timestamp + (_bidSubmissionDays * 1 days);
        procurements[_procurementId].bidDeadline = bidDeadline;
        procurements[_procurementId].status = ProcurementStatus.Published;
        
        emit ProcurementPublished(_procurementId, bidDeadline);
        emit ProcurementStatusChanged(_procurementId, ProcurementStatus.Published);
    }
    
    /**
     * @dev Submit a bid for procurement (vendors only)
     */
    function submitBid(
        string memory _procurementId,
        string memory _vendorId,
        string memory _companyName,
        uint256 _bidAmount,
        string memory _technicalProposal,
        string memory _costingDocument,
        uint256 _completionDays
    ) external onlyVendor validProcurement(_procurementId) {
        require(procurements[_procurementId].status == ProcurementStatus.Published, "Bidding not open");
        require(block.timestamp <= procurements[_procurementId].bidDeadline, "Bid deadline passed");
        require(_bidAmount > 0, "Bid amount must be positive");
        require(_completionDays > 0, "Completion days must be positive");
        require(procurementBids[_procurementId].length < procurements[_procurementId].maxBids, "Maximum bids reached");
        
        // Check if vendor already submitted a bid
        for (uint256 i = 0; i < procurementBids[_procurementId].length; i++) {
            require(
                keccak256(bytes(procurementBids[_procurementId][i].vendorId)) != keccak256(bytes(_vendorId)),
                "Vendor already submitted a bid"
            );
        }
        
        BidDetails memory newBid = BidDetails({
            vendorId: _vendorId,
            companyName: _companyName,
            bidAmount: _bidAmount,
            technicalProposal: _technicalProposal,
            costingDocument: _costingDocument,
            completionDays: _completionDays,
            submittedTimestamp: block.timestamp,
            qualityScore: 0,
            combinedScore: 0,
            isShortlisted: false,
            isWinner: false,
            remarks: ""
        });
        
        procurementBids[_procurementId].push(newBid);
        
        emit BidSubmitted(_procurementId, _vendorId, _bidAmount);
    }
    
    /**
     * @dev Close bid submission period (BPPA officer only)
     */
    function closeBidSubmission(string memory _procurementId) external onlyBppaOfficer validProcurement(_procurementId) {
        require(procurements[_procurementId].status == ProcurementStatus.Published, "Procurement not in published state");
        require(procurementBids[_procurementId].length > 0, "No bids received");
        
        procurements[_procurementId].status = ProcurementStatus.BidsReceived;
        emit ProcurementStatusChanged(_procurementId, ProcurementStatus.BidsReceived);
    }
    
    /**
     * @dev Set quality scores for QCBS method (BPPA officer only)
     */
    function setQualityScores(
        string memory _procurementId,
        uint256[] memory _bidIndices,
        uint256[] memory _qualityScores
    ) external onlyBppaOfficer validProcurement(_procurementId) {
        require(procurements[_procurementId].status == ProcurementStatus.BidsReceived, "Invalid status for quality scoring");
        require(procurements[_procurementId].shortlistMethod == ShortlistingMethod.QCBS, "Not a QCBS procurement");
        require(_bidIndices.length == _qualityScores.length, "Arrays length mismatch");
        
        for (uint256 i = 0; i < _bidIndices.length; i++) {
            require(_bidIndices[i] < procurementBids[_procurementId].length, "Invalid bid index");
            require(_qualityScores[i] <= 100, "Quality score must be 0-100");
            
            procurementBids[_procurementId][_bidIndices[i]].qualityScore = _qualityScores[i];
        }
    }
    
    /**
     * @dev Perform L1 shortlisting (BPPA officer only)
     */
    function performL1Shortlisting(string memory _procurementId) external onlyBppaOfficer validProcurement(_procurementId) {
        require(procurements[_procurementId].status == ProcurementStatus.BidsReceived, "Invalid status for shortlisting");
        require(procurements[_procurementId].shortlistMethod == ShortlistingMethod.L1, "Not an L1 procurement");
        
        uint256 bidsCount = procurementBids[_procurementId].length;
        require(bidsCount > 0, "No bids to shortlist");
        
        // Create array of bid indices and sort by bid amount (ascending)
        uint256[] memory sortedIndices = new uint256[](bidsCount);
        for (uint256 i = 0; i < bidsCount; i++) {
            sortedIndices[i] = i;
        }
        
        // Simple bubble sort by bid amount
        for (uint256 i = 0; i < bidsCount - 1; i++) {
            for (uint256 j = 0; j < bidsCount - i - 1; j++) {
                if (procurementBids[_procurementId][sortedIndices[j]].bidAmount > 
                    procurementBids[_procurementId][sortedIndices[j + 1]].bidAmount) {
                    uint256 temp = sortedIndices[j];
                    sortedIndices[j] = sortedIndices[j + 1];
                    sortedIndices[j + 1] = temp;
                }
            }
        }
        
        // Shortlist the lowest bids
        uint256 shortlistCount = procurements[_procurementId].shortlistCount;
        if (shortlistCount > bidsCount) {
            shortlistCount = bidsCount;
        }
        
        uint256[] memory shortlisted = new uint256[](shortlistCount);
        for (uint256 i = 0; i < shortlistCount; i++) {
            uint256 bidIndex = sortedIndices[i];
            procurementBids[_procurementId][bidIndex].isShortlisted = true;
            shortlistedBidIndices[_procurementId].push(bidIndex);
            shortlisted[i] = bidIndex;
        }
        
        procurements[_procurementId].status = ProcurementStatus.ShortlistDone;
        
        emit BidsShortlisted(_procurementId, shortlisted, ShortlistingMethod.L1);
        emit ProcurementStatusChanged(_procurementId, ProcurementStatus.ShortlistDone);
    }
    
    /**
     * @dev Perform QCBS shortlisting (BPPA officer only)
     */
    function performQCBSShortlisting(string memory _procurementId) external onlyBppaOfficer validProcurement(_procurementId) {
        require(procurements[_procurementId].status == ProcurementStatus.BidsReceived, "Invalid status for shortlisting");
        require(procurements[_procurementId].shortlistMethod == ShortlistingMethod.QCBS, "Not a QCBS procurement");
        
        uint256 bidsCount = procurementBids[_procurementId].length;
        require(bidsCount > 0, "No bids to shortlist");
        
        QCBSWeights memory weights = qcbsWeights[_procurementId];
        
        // Find minimum bid amount for cost score calculation
        uint256 minBidAmount = type(uint256).max;
        for (uint256 i = 0; i < bidsCount; i++) {
            if (procurementBids[_procurementId][i].bidAmount < minBidAmount) {
                minBidAmount = procurementBids[_procurementId][i].bidAmount;
            }
        }
        
        // Calculate combined scores
        for (uint256 i = 0; i < bidsCount; i++) {
            BidDetails storage bid = procurementBids[_procurementId][i];
            
            // Cost score = (min_bid / this_bid) * 100
            uint256 costScore = (minBidAmount * 100) / bid.bidAmount;
            
            // Combined score = (cost_score * cost_weight + quality_score * quality_weight) / 100
            uint256 combinedScore = (costScore * weights.costWeight + bid.qualityScore * weights.qualityWeight) / 100;
            bid.combinedScore = combinedScore;
        }
        
        // Create array of bid indices and sort by combined score (descending)
        uint256[] memory sortedIndices = new uint256[](bidsCount);
        for (uint256 i = 0; i < bidsCount; i++) {
            sortedIndices[i] = i;
        }
        
        // Simple bubble sort by combined score (descending)
        for (uint256 i = 0; i < bidsCount - 1; i++) {
            for (uint256 j = 0; j < bidsCount - i - 1; j++) {
                if (procurementBids[_procurementId][sortedIndices[j]].combinedScore < 
                    procurementBids[_procurementId][sortedIndices[j + 1]].combinedScore) {
                    uint256 temp = sortedIndices[j];
                    sortedIndices[j] = sortedIndices[j + 1];
                    sortedIndices[j + 1] = temp;
                }
            }
        }
        
        // Shortlist the highest scoring bids
        uint256 shortlistCount = procurements[_procurementId].shortlistCount;
        if (shortlistCount > bidsCount) {
            shortlistCount = bidsCount;
        }
        
        uint256[] memory shortlisted = new uint256[](shortlistCount);
        for (uint256 i = 0; i < shortlistCount; i++) {
            uint256 bidIndex = sortedIndices[i];
            procurementBids[_procurementId][bidIndex].isShortlisted = true;
            shortlistedBidIndices[_procurementId].push(bidIndex);
            shortlisted[i] = bidIndex;
        }
        
        procurements[_procurementId].status = ProcurementStatus.ShortlistDone;
        
        emit BidsShortlisted(_procurementId, shortlisted, ShortlistingMethod.QCBS);
        emit ProcurementStatusChanged(_procurementId, ProcurementStatus.ShortlistDone);
    }
    
    /**
     * @dev Mark procurement as ready for voting (BPPA officer only)
     */
    function markReadyForVoting(string memory _procurementId) external onlyBppaOfficer validProcurement(_procurementId) {
        require(procurements[_procurementId].status == ProcurementStatus.ShortlistDone, "Shortlisting not done");
        require(shortlistedBidIndices[_procurementId].length > 0, "No shortlisted bids");
        
        procurements[_procurementId].status = ProcurementStatus.VotingActive;
        emit ProcurementStatusChanged(_procurementId, ProcurementStatus.VotingActive);
    }
    
    /**
     * @dev Award procurement to winning bid (BPPA officer only)
     */
    function awardProcurement(
        string memory _procurementId,
        uint256 _winningBidIndex,
        string memory _remarks
    ) external onlyBppaOfficer validProcurement(_procurementId) {
        require(procurements[_procurementId].status == ProcurementStatus.VotingActive, "Voting not completed");
        require(_winningBidIndex < procurementBids[_procurementId].length, "Invalid winning bid index");
        require(procurementBids[_procurementId][_winningBidIndex].isShortlisted, "Winning bid not shortlisted");
        
        // Mark the winning bid
        procurementBids[_procurementId][_winningBidIndex].isWinner = true;
        procurementBids[_procurementId][_winningBidIndex].remarks = _remarks;
        winningBidIndex[_procurementId] = _winningBidIndex;
        
        procurements[_procurementId].status = ProcurementStatus.Awarded;
        
        emit ProcurementAwarded(_procurementId, _winningBidIndex, procurementBids[_procurementId][_winningBidIndex].vendorId);
        emit ProcurementStatusChanged(_procurementId, ProcurementStatus.Awarded);
    }
    
    /**
     * @dev Get procurement details
     */
    function getProcurement(string memory _procurementId) external view returns (
        string memory procurementId,
        string memory title,
        string memory description,
        uint256 estimatedValue,
        string memory category,
        uint256 bidDeadline,
        ProcurementStatus status,
        ShortlistingMethod shortlistMethod,
        uint256 bidsCount
    ) {
        ProcurementDetails memory proc = procurements[_procurementId];
        return (
            proc.procurementId,
            proc.title,
            proc.description,
            proc.estimatedValue,
            proc.category,
            proc.bidDeadline,
            proc.status,
            proc.shortlistMethod,
            procurementBids[_procurementId].length
        );
    }
    
    /**
     * @dev Get all bids for a procurement
     */
    function getBidsForProcurement(string memory _procurementId) external view returns (BidDetails[] memory) {
        return procurementBids[_procurementId];
    }
    
    /**
     * @dev Get shortlisted bid indices
     */
    function getShortlistedBids(string memory _procurementId) external view returns (uint256[] memory) {
        return shortlistedBidIndices[_procurementId];
    }
    
    /**
     * @dev Get all procurement IDs
     */
    function getAllProcurements() external view returns (string[] memory) {
        return allProcurementIds;
    }
    
    /**
     * @dev Get active procurements (published and accepting bids)
     */
    function getActiveProcurements() external view returns (string[] memory) {
        uint256 activeCount = 0;
        
        // Count active procurements
        for (uint256 i = 0; i < allProcurementIds.length; i++) {
            if (procurements[allProcurementIds[i]].status == ProcurementStatus.Published &&
                block.timestamp <= procurements[allProcurementIds[i]].bidDeadline) {
                activeCount++;
            }
        }
        
        // Create array of active procurement IDs
        string[] memory activeProcurements = new string[](activeCount);
        uint256 currentIndex = 0;
        
        for (uint256 i = 0; i < allProcurementIds.length; i++) {
            if (procurements[allProcurementIds[i]].status == ProcurementStatus.Published &&
                block.timestamp <= procurements[allProcurementIds[i]].bidDeadline) {
                activeProcurements[currentIndex] = allProcurementIds[i];
                currentIndex++;
            }
        }
        
        return activeProcurements;
    }
}

contracts/ProcurementVoting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title ProcurementVoting
 * @dev Smart contract for transparent public voting on procurement bids
 * Citizens can vote on shortlisted bids
 */
contract ProcurementVoting {
    
    struct Procurement {
        string procurementId;      // Government procurement reference number
        string title;              // Procurement title
        string description;        // Procurement description
        uint256 estimatedValue;    // Estimated project value in BDT
        string category;           // Infrastructure, IT, Services, etc.
        address bppaOfficer;       // BPPA officer who created this procurement
        uint256 createdTimestamp;  // When procurement was created
        uint256 votingEnds;        // Voting deadline
        bool isActive;             // Whether voting is active
        bool isCompleted;          // Whether voting has concluded
        uint256 winningBidIndex;   // Index of winning bid (0 if no winner yet)
    }
    
    struct Bid {
        string vendorId;           // Vendor identification
        string companyName;        // Vendor company name
        uint256 bidAmount;         // Bid amount in BDT
        string technicalProposal;  // Brief technical proposal
        string costingDocument;    // IPFS hash for detailed costing PDF
        uint256 completionDays;    // Project completion time in days
        address bppaOfficer;       // BPPA officer who shortlisted this bid
        uint256 shortlistedTimestamp; // When bid was shortlisted
        uint256 votesYes;          // Number of YES votes
        uint256 votesNo;           // Number of NO votes
    }
    
    struct Vote {
        address citizen;           // Citizen's address
        bool vote;                // true for YES, false for NO
        uint256 timestamp;        // When the vote was cast
    }
    
    struct Citizen {
        address citizenAddress;
        string tiin;               // Taxpayer Identification Number
        string fullName;
        bool isRegistered;         // Whether citizen is registered for voting
        bool isActive;             // Whether citizen can vote
    }
    
    // State variables
    mapping(string => Procurement) public procurements;
    mapping(string => Bid[]) public procurementBids; // procurementId -> array of bids
    mapping(string => mapping(uint256 => mapping(address => Vote))) public votes; // procurementId -> bidIndex -> citizen -> vote
    mapping(string => mapping(uint256 => mapping(address => bool))) public hasVoted; // procurementId -> bidIndex -> citizen -> hasVoted
    mapping(address => Citizen) public citizens;
    mapping(address => bool) public isBppaOfficer;
    mapping(address => bool) public isRegisteredCitizen;
    
    // Arrays to track data
    string[] public allProcurementIds;
    address[] public allCitizens;
    
    // Events
    event ProcurementCreated(string indexed procurementId, string title, address bppaOfficer);
    event BidShortlisted(string indexed procurementId, uint256 bidIndex, string vendorId);
    event VotingStarted(string indexed procurementId, uint256 votingEnds);
    event VoteCast(string indexed procurementId, uint256 bidIndex, address citizen, bool vote);
    event VotingCompleted(string indexed procurementId, uint256 winningBidIndex);
    event CitizenRegistered(address indexed citizen, string tiin);
    event BppaOfficerRegistered(address indexed officer);
    
    // Modifiers
    modifier onlyBppaOfficer() {
        require(isBppaOfficer[msg.sender], "Only BPPA officers can perform this action");
        _;
    }
    
    modifier onlyRegisteredCitizen() {
        require(isRegisteredCitizen[msg.sender], "Only registered citizens can vote");
        require(citizens[msg.sender].isActive, "Citizen is not active");
        _;
    }
    
    modifier onlyAdmin() {
        // In a real deployment, this should be restricted to contract owner or admin
        _;
    }
    
    /**
     * @dev Constructor
     */
    constructor() {
        // Contract is deployed and ready to use
    }
    
    /**
     * @dev Register a BPPA officer (admin function)
     */
    function registerBppaOfficer(address _officerAddress) external onlyAdmin {
        require(_officerAddress != address(0), "Invalid officer address");
        require(!isBppaOfficer[_officerAddress], "Officer already registered");
        
        isBppaOfficer[_officerAddress] = true;
        emit BppaOfficerRegistered(_officerAddress);
    }
    
    /**
     * @dev Register a citizen for voting (admin function or self-registration)
     */
    function registerCitizen(
        address _citizenAddress,
        string memory _tiin,
        string memory _fullName
    ) external {
        require(_citizenAddress != address(0), "Invalid citizen address");
        require(!isRegisteredCitizen[_citizenAddress], "Citizen already registered");
        
        citizens[_citizenAddress] = Citizen({
            citizenAddress: _citizenAddress,
            tiin: _tiin,
            fullName: _fullName,
            isRegistered: true,
            isActive: true
        });
        
        isRegisteredCitizen[_citizenAddress] = true;
        allCitizens.push(_citizenAddress);
        
        emit CitizenRegistered(_citizenAddress, _tiin);
    }
    
    /**
     * @dev Create a new procurement (BPPA officer only)
     */
    function createProcurement(
        string memory _procurementId,
        string memory _title,
        string memory _description,
        uint256 _estimatedValue,
        string memory _category
    ) external onlyBppaOfficer {
        require(bytes(_procurementId).length > 0, "Procurement ID cannot be empty");
        require(procurements[_procurementId].createdTimestamp == 0, "Procurement already exists");
        
        procurements[_procurementId] = Procurement({
            procurementId: _procurementId,
            title: _title,
            description: _description,
            estimatedValue: _estimatedValue,
            category: _category,
            bppaOfficer: msg.sender,
            createdTimestamp: block.timestamp,
            votingEnds: 0,
            isActive: false,
            isCompleted: false,
            winningBidIndex: 0
        });
        
        allProcurementIds.push(_procurementId);
        
        emit ProcurementCreated(_procurementId, _title, msg.sender);
    }
    
    /**
     * @dev Shortlist a bid for public voting (BPPA officer only)
     */
    function shortlistBid(
        string memory _procurementId,
        string memory _vendorId,
        string memory _companyName,
        uint256 _bidAmount,
        string memory _technicalProposal,
        string memory _costingDocument,
        uint256 _completionDays
    ) external onlyBppaOfficer {
        require(procurements[_procurementId].createdTimestamp != 0, "Procurement not found");
        require(!procurements[_procurementId].isCompleted, "Procurement voting already completed");
        require(procurementBids[_procurementId].length < 4, "Maximum 4 bids can be shortlisted");
        
        Bid memory newBid = Bid({
            vendorId: _vendorId,
            companyName: _companyName,
            bidAmount: _bidAmount,
            technicalProposal: _technicalProposal,
            costingDocument: _costingDocument,
            completionDays: _completionDays,
            bppaOfficer: msg.sender,
            shortlistedTimestamp: block.timestamp,
            votesYes: 0,
            votesNo: 0
        });
        
        procurementBids[_procurementId].push(newBid);
        uint256 bidIndex = procurementBids[_procurementId].length - 1;
        
        emit BidShortlisted(_procurementId, bidIndex, _vendorId);
    }
    
    /**
     * @dev Start voting for a procurement (BPPA officer only)
     */
    function startVoting(string memory _procurementId, uint256 _votingDurationDays) external onlyBppaOfficer {
        require(procurements[_procurementId].createdTimestamp != 0, "Procurement not found");
        require(!procurements[_procurementId].isActive, "Voting already started");
        require(!procurements[_procurementId].isCompleted, "Procurement already completed");
        require(procurementBids[_procurementId].length > 0, "No bids shortlisted");
        
        uint256 votingEnds = block.timestamp + (_votingDurationDays * 1 days);
        procurements[_procurementId].votingEnds = votingEnds;
        procurements[_procurementId].isActive = true;
        
        emit VotingStarted(_procurementId, votingEnds);
    }
    
    /**
     * @dev Cast a vote for a specific bid (registered citizens only)
     */
    function castVote(
        string memory _procurementId,
        uint256 _bidIndex,
        bool _vote
    ) external onlyRegisteredCitizen {
        require(procurements[_procurementId].isActive, "Voting is not active for this procurement");
        require(block.timestamp <= procurements[_procurementId].votingEnds, "Voting period has ended");
        require(_bidIndex < procurementBids[_procurementId].length, "Invalid bid index");
        require(!hasVoted[_procurementId][_bidIndex][msg.sender], "Already voted for this bid");
        
        votes[_procurementId][_bidIndex][msg.sender] = Vote({
            citizen: msg.sender,
            vote: _vote,
            timestamp: block.timestamp
        });
        
        hasVoted[_procurementId][_bidIndex][msg.sender] = true;
        
        if (_vote) {
            procurementBids[_procurementId][_bidIndex].votesYes++;
        } else {
            procurementBids[_procurementId][_bidIndex].votesNo++;
        }
        
        emit VoteCast(_procurementId, _bidIndex, msg.sender, _vote);
    }
    
    /**
     * @dev Complete voting and determine winner (BPPA officer only)
     */
    function completeVoting(string memory _procurementId) external onlyBppaOfficer {
        require(procurements[_procurementId].isActive, "Voting is not active");
        require(block.timestamp > procurements[_procurementId].votingEnds, "Voting period has not ended");
        require(!procurements[_procurementId].isCompleted, "Voting already completed");
        
        // Find the bid with the highest YES votes
        uint256 maxVotes = 0;
        uint256 winningIndex = 0;
        
        for (uint256 i = 0; i < procurementBids[_procurementId].length; i++) {
            if (procurementBids[_procurementId][i].votesYes > maxVotes) {
                maxVotes = procurementBids[_procurementId][i].votesYes;
                winningIndex = i;
            }
        }
        
        procurements[_procurementId].winningBidIndex = winningIndex;
        procurements[_procurementId].isActive = false;
        procurements[_procurementId].isCompleted = true;
        
        emit VotingCompleted(_procurementId, winningIndex);
    }
    
    /**
     * @dev Get procurement details
     */
    function getProcurement(string memory _procurementId) external view returns (
        string memory procurementId,
        string memory title,
        string memory description,
        uint256 estimatedValue,
        string memory category,
        address bppaOfficer,
        uint256 createdTimestamp,
        uint256 votingEnds,
        bool isActive,
        bool isCompleted,
        uint256 winningBidIndex
    ) {
        Procurement memory proc = procurements[_procurementId];
        return (
            proc.procurementId,
            proc.title,
            proc.description,
            proc.estimatedValue,
            proc.category,
            proc.bppaOfficer,
            proc.createdTimestamp,
            proc.votingEnds,
            proc.isActive,
            proc.isCompleted,
            proc.winningBidIndex
        );
    }
    
    /**
     * @dev Get shortlisted bids for a procurement
     */
    function getBidsForProcurement(string memory _procurementId) external view returns (Bid[] memory) {
        return procurementBids[_procurementId];
    }
    
    /**
     * @dev Get specific bid details
     */
    function getBid(string memory _procurementId, uint256 _bidIndex) external view returns (
        string memory vendorId,
        string memory companyName,
        uint256 bidAmount,
        string memory technicalProposal,
        string memory costingDocument,
        uint256 completionDays,
        address bppaOfficer,
        uint256 shortlistedTimestamp,
        uint256 votesYes,
        uint256 votesNo
    ) {
        require(_bidIndex < procurementBids[_procurementId].length, "Invalid bid index");
        Bid memory bid = procurementBids[_procurementId][_bidIndex];
        return (
            bid.vendorId,
            bid.companyName,
            bid.bidAmount,
            bid.technicalProposal,
            bid.costingDocument,
            bid.completionDays,
            bid.bppaOfficer,
            bid.shortlistedTimestamp,
            bid.votesYes,
            bid.votesNo
        );
    }
    
    /**
     * @dev Get vote results for a specific bid
     */
    function getVoteResults(string memory _procurementId, uint256 _bidIndex) external view returns (
        uint256 votesYes,
        uint256 votesNo,
        uint256 totalVotes,
        uint256 yesPercentage
    ) {
        require(_bidIndex < procurementBids[_procurementId].length, "Invalid bid index");
        Bid memory bid = procurementBids[_procurementId][_bidIndex];
        
        uint256 total = bid.votesYes + bid.votesNo;
        uint256 percentage = total > 0 ? (bid.votesYes * 100) / total : 0;
        
        return (bid.votesYes, bid.votesNo, total, percentage);
    }
    
    /**
     * @dev Check if citizen has voted for a specific bid
     */
    function hasVotedForBid(
        string memory _procurementId,
        uint256 _bidIndex,
        address _citizen
    ) external view returns (bool) {
        return hasVoted[_procurementId][_bidIndex][_citizen];
    }
    
    /**
     * @dev Get all active procurements (voting ongoing)
     */
    function getActiveProcurements() external view returns (string[] memory) {
        uint256 activeCount = 0;
        
        // Count active procurements
        for (uint256 i = 0; i < allProcurementIds.length; i++) {
            if (procurements[allProcurementIds[i]].isActive && 
                block.timestamp <= procurements[allProcurementIds[i]].votingEnds) {
                activeCount++;
            }
        }
        
        // Create array of active procurement IDs
        string[] memory activeProcurements = new string[](activeCount);
        uint256 currentIndex = 0;
        
        for (uint256 i = 0; i < allProcurementIds.length; i++) {
            if (procurements[allProcurementIds[i]].isActive && 
                block.timestamp <= procurements[allProcurementIds[i]].votingEnds) {
                activeProcurements[currentIndex] = allProcurementIds[i];
                currentIndex++;
            }
        }
        
        return activeProcurements;
    }
    
    /**
     * @dev Get total number of procurements
     */
    function getTotalProcurements() external view returns (uint256) {
        return allProcurementIds.length;
    }
    
    /**
     * @dev Get total number of registered citizens
     */
    function getTotalRegisteredCitizens() external view returns (uint256) {
        return allCitizens.length;
    }
}

contracts/TaxReturnRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// Import National Ledger interface
interface INationalLedger {
    function addRevenue(
        string memory _tiin,
        uint256 _amount,
        string memory _fiscalYear,
        address _validator,
        string memory _taxReturnHash
    ) external;
}

/**
 * @title TaxReturnRegistry
 * @dev Smart contract for storing and validating tax returns on a private blockchain
 * Only NBR officers can validate tax returns
 * Automatically adds verified tax amounts to National Revenue Ledger
 */
contract TaxReturnRegistry {
    
    struct TaxReturn {
        string tiin;                // Taxpayer Identification Number
        string fiscalYear;          // e.g., "2023-24"
        string ipfsHash;           // IPFS hash of the tax return PDF
        uint256 totalIncome;       // Total income in BDT (stored as wei equivalent)
        uint256 totalCost;         // Total tax owed in BDT (stored as wei equivalent)
        address validator;         // NBR officer who validated this return
        uint256 timestamp;         // When the return was submitted
        uint256 validatedTimestamp; // When the return was validated
        bool isValidated;          // Whether the return has been validated
        bool isApproved;           // Whether the return was approved or declined
        string comments;           // NBR officer's comments
    }
    
    struct NbrOfficer {
        address officerAddress;
        string officerId;
        string fullName;
        bool isActive;
    }
    
    // Mappings
    mapping(bytes32 => TaxReturn) public taxReturns;
    mapping(address => NbrOfficer) public nbrOfficers;
    mapping(address => bool) public isNbrOfficer;
    
    // Arrays to track all returns and officers
    bytes32[] public allTaxReturnKeys;
    address[] public allOfficers;
    
    // National Ledger integration
    INationalLedger public nationalLedger;
    bool public nationalLedgerEnabled;
    
    // Events
    event TaxReturnSubmitted(bytes32 indexed returnKey, string tiin, string fiscalYear, string ipfsHash);
    event TaxReturnValidated(bytes32 indexed returnKey, address validator, bool approved);
    event RevenueAddedToNationalLedger(bytes32 indexed returnKey, uint256 amount, string fiscalYear);
    event NbrOfficerRegistered(address indexed officer, string officerId);
    event NbrOfficerDeactivated(address indexed officer);
    event NationalLedgerUpdated(address indexed newLedger);
    
    // Modifiers
    modifier onlyNbrOfficer() {
        require(isNbrOfficer[msg.sender], "Only NBR officers can perform this action");
        require(nbrOfficers[msg.sender].isActive, "NBR officer is not active");
        _;
    }
    
    modifier onlyAdmin() {
        // In a real deployment, this should be restricted to contract owner or admin
        _;
    }
    
    address public admin;
    
    modifier onlyContractAdmin() {
        require(msg.sender == admin, "Only contract admin can perform this action");
        _;
    }
    
    /**
     * @dev Constructor - deploys the contract
     */
    constructor() {
        admin = msg.sender;
        nationalLedgerEnabled = false;
    }
    
    /**
     * @dev Set National Ledger contract address (admin only)
     */
    function setNationalLedger(address _nationalLedgerAddress) external onlyContractAdmin {
        require(_nationalLedgerAddress != address(0), "Invalid national ledger address");
        nationalLedger = INationalLedger(_nationalLedgerAddress);
        nationalLedgerEnabled = true;
        emit NationalLedgerUpdated(_nationalLedgerAddress);
    }
    
    /**
     * @dev Enable/disable National Ledger integration (admin only)
     */
    function toggleNationalLedger(bool _enabled) external onlyContractAdmin {
        nationalLedgerEnabled = _enabled;
    }
    
    /**
     * @dev Register a new NBR officer (admin function)
     */
    function registerNbrOfficer(
        address _officerAddress,
        string memory _officerId,
        string memory _fullName
    ) external onlyAdmin {
        require(_officerAddress != address(0), "Invalid officer address");
        require(!isNbrOfficer[_officerAddress], "Officer already registered");
        
        nbrOfficers[_officerAddress] = NbrOfficer({
            officerAddress: _officerAddress,
            officerId: _officerId,
            fullName: _fullName,
            isActive: true
        });
        
        isNbrOfficer[_officerAddress] = true;
        allOfficers.push(_officerAddress);
        
        emit NbrOfficerRegistered(_officerAddress, _officerId);
    }
    
    /**
     * @dev Deactivate an NBR officer (admin function)
     */
    function deactivateNbrOfficer(address _officerAddress) external onlyAdmin {
        require(isNbrOfficer[_officerAddress], "Officer not found");
        nbrOfficers[_officerAddress].isActive = false;
        emit NbrOfficerDeactivated(_officerAddress);
    }
    
    /**
     * @dev Submit a new tax return (called by backend service)
     */
    function submitTaxReturn(
        string memory _tiin,
        string memory _fiscalYear,
        string memory _ipfsHash,
        uint256 _totalIncome,
        uint256 _totalCost
    ) external returns (bytes32) {
        // Generate unique key for this tax return
        bytes32 returnKey = keccak256(abi.encodePacked(_tiin, _fiscalYear));
        
        require(taxReturns[returnKey].timestamp == 0, "Tax return already exists for this TIIN and fiscal year");
        require(bytes(_tiin).length > 0, "TIIN cannot be empty");
        require(bytes(_fiscalYear).length > 0, "Fiscal year cannot be empty");
        require(bytes(_ipfsHash).length > 0, "IPFS hash cannot be empty");
        
        taxReturns[returnKey] = TaxReturn({
            tiin: _tiin,
            fiscalYear: _fiscalYear,
            ipfsHash: _ipfsHash,
            totalIncome: _totalIncome,
            totalCost: _totalCost,
            validator: address(0),
            timestamp: block.timestamp,
            validatedTimestamp: 0,
            isValidated: false,
            isApproved: false,
            comments: ""
        });
        
        allTaxReturnKeys.push(returnKey);
        
        emit TaxReturnSubmitted(returnKey, _tiin, _fiscalYear, _ipfsHash);
        return returnKey;
    }
    
    /**
     * @dev Validate a tax return (NBR officer only)
     */
    function validateTaxReturn(
        bytes32 _returnKey,
        bool _approved,
        string memory _comments
    ) external onlyNbrOfficer {
        require(taxReturns[_returnKey].timestamp != 0, "Tax return not found");
        require(!taxReturns[_returnKey].isValidated, "Tax return already validated");
        
        taxReturns[_returnKey].validator = msg.sender;
        taxReturns[_returnKey].validatedTimestamp = block.timestamp;
        taxReturns[_returnKey].isValidated = true;
        taxReturns[_returnKey].isApproved = _approved;
        taxReturns[_returnKey].comments = _comments;
        
        // If approved and National Ledger is enabled, add to revenue
        if (_approved && nationalLedgerEnabled && address(nationalLedger) != address(0)) {
            try nationalLedger.addRevenue(
                taxReturns[_returnKey].tiin,
                taxReturns[_returnKey].totalCost, // Tax amount owed
                taxReturns[_returnKey].fiscalYear,
                msg.sender,
                taxReturns[_returnKey].ipfsHash
            ) {
                emit RevenueAddedToNationalLedger(_returnKey, taxReturns[_returnKey].totalCost, taxReturns[_returnKey].fiscalYear);
            } catch {
                // Log error but don't fail the validation
                // This ensures tax validation still works even if National Ledger fails
            }
        }
        
        emit TaxReturnValidated(_returnKey, msg.sender, _approved);
    }
    
    /**
     * @dev Get tax return details
     */
    function getTaxReturn(bytes32 _returnKey) external view returns (
        string memory tiin,
        string memory fiscalYear,
        string memory ipfsHash,
        uint256 totalIncome,
        uint256 totalCost,
        address validator,
        uint256 timestamp,
        uint256 validatedTimestamp,
        bool isValidated,
        bool isApproved,
        string memory comments
    ) {
        TaxReturn memory taxReturn = taxReturns[_returnKey];
        return (
            taxReturn.tiin,
            taxReturn.fiscalYear,
            taxReturn.ipfsHash,
            taxReturn.totalIncome,
            taxReturn.totalCost,
            taxReturn.validator,
            taxReturn.timestamp,
            taxReturn.validatedTimestamp,
            taxReturn.isValidated,
            taxReturn.isApproved,
            taxReturn.comments
        );
    }
    
    /**
     * @dev Get tax return key for a specific TIIN and fiscal year
     */
    function getTaxReturnKey(string memory _tiin, string memory _fiscalYear) external pure returns (bytes32) {
        return keccak256(abi.encodePacked(_tiin, _fiscalYear));
    }
    
    /**
     * @dev Get all pending tax returns (not yet validated)
     */
    function getPendingTaxReturns() external view returns (bytes32[] memory) {
        uint256 pendingCount = 0;
        
        // Count pending returns
        for (uint256 i = 0; i < allTaxReturnKeys.length; i++) {
            if (!taxReturns[allTaxReturnKeys[i]].isValidated) {
                pendingCount++;
            }
        }
        
        // Create array of pending returns
        bytes32[] memory pendingReturns = new bytes32[](pendingCount);
        uint256 currentIndex = 0;
        
        for (uint256 i = 0; i < allTaxReturnKeys.length; i++) {
            if (!taxReturns[allTaxReturnKeys[i]].isValidated) {
                pendingReturns[currentIndex] = allTaxReturnKeys[i];
                currentIndex++;
            }
        }
        
        return pendingReturns;
    }
    
    /**
     * @dev Get total number of tax returns
     */
    function getTotalTaxReturns() external view returns (uint256) {
        return allTaxReturnKeys.length;
    }
    
    /**
     * @dev Get total number of NBR officers
     */
    function getTotalNbrOfficers() external view returns (uint256) {
        return allOfficers.length;
    }
}

